import React, { useState, useEffect } from "react";
import { Link } from "react-router-dom";
import "./Charts.css";
import { searchdates } from "../src/constants/datesCompany";
import axios from "axios";
import { Line } from "react-chartjs-2";
import "chart.js/auto";
import Chart from "chart.js/auto";
import { ZoomPlugin } from "chartjs-plugin-zoom";
function Charts() {
  const [bestMatches, setBestMatches] = useState([]);
  const [charmData, setCharmData] = useState({});
  const [loading, setLoading] = useState(true);
  const [value, setValue] = useState("");

  useEffect(() => {
    // const apiKey = "ck0lj1hr01qtrbkm4og0ck0lj1hr01qtrbkm4ogg";
    const apiKey = "ck0lj1hr01qtrbkm4og0ck0lj1hr01qtrbkm4ogg";
    //const candlesApiUrl = `https://finnhub.io/api/v1/stock/candle?symbol=AAPL&resolution=1&from1693752546&token=${apiKey}`;
    //const symbol = "AAPL"; // Replace with the stock symbol you want to fetch data for
    const interval = "D"; // Adjust the interval as needed (D for daily)

    axios
      .get(
        //`https://finnhub.io/api/v1/stock/candle?symbol=${symbol}&resolution=${interval}&count=30&token=${apiKey}`
        `https://finnhub.io/api/v1/stock/candle?symbol=${value}&resolution=${interval}&count=30&token=${apiKey}`
      )
      .then((response) => {
        setCharmData(response.data);
        console.log(response);
        setLoading(false);
      })
      .catch((error) => {
        console.error("Error fetching data:", error);
        setLoading(false);
      });
  }, [value]);

  const chartData = {
    labels: charmData.t
      ? charmData.t.map((timestamp) => {
          const date = new Date(timestamp * 1000); // Convert Unix timestamp to milliseconds
          const day = date.getDate().toString().padStart(2, "0"); // Format day with leading zero
          const month = (date.getMonth() + 1).toString().padStart(2, "0"); // Month is zero-based
          const year = date.getFullYear();
          return `${day}/${month}/${year}`;
        })
      : [],

    datasets: [
      {
        label: "Stock Price",
        data: charmData.c || [],
        fill: true,

        borderColor: "rgba(75,192,192,1)",
        lineTension: 0.1,
      },
    ],
  };
  const chartOptions = {
    xAxes: {
      title: "time",
      gridThickness: 2,
      unit: "day",
      unitStepSize: 1000,
      type: "time",
      time: {
        displayFormats: {
          millisecond: "MMM DD",
          second: "MMM DD",
          minute: "MMM DD",
          hour: "MMM DD",
          day: "MMM DD",
          week: "MMM DD",
          month: "MMM DD",
          quarter: "MMM DD",
          year: "MMM DD",
        },
      },
    },
    ticks: {
      font: {
        size: 12, // Adjust the font size as needed
        family: "Arial, sans-serif", // Adjust the font family as needed
      },
    },
    plugins: {
      tooltip: {
        enabled: true,
        mode: "index",
        intersect: false,
        backgroundColor: "rgba(0,0,0,0.7)",
        titleFontColor: "#fff",
        bodyFontColor: "#fff",
      },
    },
  };
  const fetchMatchingCompanies = (searchTerm) => {
    const apiKey = "cck1bl1hr01qnva7rk7fgck1bl1hr01qnva7rk7g0"; // Replace with your Finnhub API key
    const url = `https://finnhub.io/api/v1/search?q=${searchTerm}&token=${apiKey}`;

    axios
      .get(url)
      .then((response) => {
        setBestMatches(response.data.result || []);
        console.log(response.data.result);
        setLoading(false);
      })
      .catch((error) => {
        console.error("Error fetching companies:", error);
        setLoading(false);
      });
  };

  const onChange = (event) => {
    setValue(event.target.value);
  };
  const onSearch = (searchTerm) => {
    console.log("search", searchTerm);
    setValue(searchTerm);
    fetchMatchingCompanies(searchTerm);
  };

  return (
    <div>
      <div className="header">
        <div className="left">
          <img src="/stock-logo.svg" alt="Stock Tracker Logo" />
          <h1>Stock Tracker</h1>
        </div>

        <button className="scroll-to-top-button fade-out" id="scroll">
          â†‘
        </button>

        <div className="right">
          <Link to="/">
            <button>Return to Home</button>
          </Link>
        </div>
      </div>

      <div className="containerSearch">
        <input
          className="search-input"
          type="text"
          value={value}
          placeholder="Search stock.."
          onChange={onChange}
        ></input>

        <button className="search-button " onClick={() => onSearch(value)}>
          Search
        </button>
      </div>
      <div className="containerResults">
        {bestMatches
          .filter((item) => {
            const searchTerm = value.toLowerCase();
            const companyName = item.symbol.toLowerCase();
            return (
              searchTerm &&
              companyName.startsWith(searchTerm) &&
              companyName !== searchTerm
            );
          })

          .map((item) => (
            <li
              className="itemsList"
              key={item.symbol}
              onClick={() => onSearch(item.symbol)}
            >
              {item.symbol}
            </li>
          ))}
      </div>
      <h2 className="companyName">{value}</h2>
      <div>
        <div className="graph">
          {loading ? (
            <p>Loading...</p>
          ) : (
            <Line data={chartData} options={chartOptions} />
          )}
        </div>
      </div>
    </div>
  );
}

export default Charts;
